mapper.update:
  update cable_qrcode

set      qr_image = #{qr_image}
  where id = #{qr_id}

mapper.select:
	SELECT
@rownum:=@rownum+1 AS rownum, b.* FROM (
  SELECT A.id eqp_id,C.group_name,A.eqp_name,A.eqp_direct,A.hostname,B.`name` rack_name,A.unit_position,rack_id,m_company,yearofintroduct,model FROM equipment A
JOIN rack B ON B.id = A.rack_id
JOIN bizgroup C ON C.id = A.group_id
)b ,(SELECT @rownum:=#{offset}) TMP
LIMIT #{offset},#{limit};
