mapper.select:
/* getSelectAssetData
장비관리 > 장비목록 > 추가/수정/상세 페이지 > 장비분류 > 자산분류 */
select
    asset_id as `id`, asset_category as `name`
from
    equipment_categories
where
    config_id = #{id}
group by asset_id;

/* getSelectSubData
장비관리 > 장비목록 > 추가/수정/상세 페이지 > 장비분류 > 자산세부분류 */
select
    sub_id as `id`, sub_category as `name`, categories
from
    equipment_categories
where
    asset_id = #{id}
GROUP BY sub_id;

/* getSelectDetailData
장비관리 > 장비목록 > 추가/수정/상세 페이지 > 장비분류 > 자산상세분류 */
select
    detail_id as `id`, detail_category as `name`
from
    equipment_categories
where
    sub_id = #{id}
GROUP BY detail_id;

/* getEqpTotalList ... 장비관리 > 장비목록 > 리스트 목록 */ SELECT A.eqp_manage_id /* 장비 관리번호 */ , A.eqp_name /* 장비명 */ , A.host_name /* 호스트명 */ , A.model_name /* 모델명 */ , A.m_company /* 제조사 */ , A.domestic /* 국산여부 */ , A.redundancy_config /* 이중화구성여부 */ , A.maintenance_contract_target /* 유지관리 계약대상여부 */ , A.config_id /* 구성분류 */ , A.asset_id /* 자산분류 */ , A.sub_id /* 자산세부분류 */ , A.detail_id /* 자산상세분류 */ , A.network_operation_type /* 네트워크 운영 구분 */ , A.operating_status /* 운영상태 */ , A.operating_department /* 운영부서 */ , A.primary_operator /* 운영담당자(정) */ , A.secondary_operator /* 운영담당자(부) */ , A.primary_outsourced_operator /* 위탁운영사용자(정) */ , A.secondary_outsourced_operator /* 위탁운영사용자(부) */ , A.created_at /* 생성일 */ , A.updated_at /* 수정일 */ , A.creator /* 등록자 */ FROM equipment_basic A WHERE 1=1
AND ( BINARY A.eqp_name Like CONCAT('%', #{searchEqpSearchInput}, '%') OR BINARY A.hostname Like CONCAT('%', #{searchEqpSearchInput}, '%') OR BINARY A.m_company Like CONCAT('%', #{searchEqpSearchInput}, '%') OR BINARY A.model Like CONCAT('%', #{searchEqpSearchInput}, '%') )
LIMIT #{offset},#{limit};

mapper.update:
/* updateEqpList ...
    장비관리 > 장비목록 > 수정 */
UPDATE equipment
SET
    eqp_name = #{eqp_name}

    , config_category = #{config_category}


    , asset_id = #{asset_id}


    , m_company = #{m_company}


    , operating_department = #{operating_department}


    , primary_operator = #{primary_operator}


    , secondary_operator = #{secondary_operator}


    , primary_outsourced_operator = #{primary_outsourced_operator}


    , secondary_outsourced_operator = #{secondary_outsourced_operator}


    , maintenance_contract_target = #{maintenance_contract_target}


    , redundancy_config = #{redundancy_config}


    , domestic = #{domestic}


    , asset_category = #{asset_category}


    , config_id = #{config_id}


    , model = #{model}


    , operating_status = #{operating_status}


    , asset_acquisition_date = #{asset_acquisition_date}


    , asset_disposal_date = #{asset_disposal_date}


    , yearofintroduct = #{yearofintroduct}


    , acquisition_cost = #{acquisition_cost}


    , unit_position = #{unit_position}


    , dbrain_number = #{dbrain_number}


    , eol_status = #{eol_status}


    , eos_status = #{eos_status}


    , network_operation_type = #{network_operation_type}


    , hostname = #{hostname}


    , ip_address = #{ip_address}


    , serial_number = #{serial_number}


    , installation_coordinates = #{installation_coordinates}


    , installation_units = #{installation_units}


    , resource_name = #{resource_name}


    , os_version = #{os_version}


    , cpu = #{cpu}


    , mem = #{mem}


    , disk = #{disk}


    , equipment_size_units = #{equipment_size_units}

WHERE id = #{eqp_id};

mapper.delete:
/* deleteEqpList ...
    장비관리 > 장비목록 > 삭제*/
delete from equipment
where id = #{deleteEqpTarget}
