mapper.update:
/* updateEquipmentLink...
    장비관리 > 장비목록 > 추가/수정 페이지 > 저장 > 장비 연결정보 수정
*/
update
    equipment_link
set
    host = #{host},
    ip_address = #{ip_address},
    port = #{port}
where
    id = #{id}
    and eqp_manage_id = #{eqp_manage_id}

mapper.delete:
/* deleteEquipmentLink...
    장비관리 > 장비목록 > 추가/수정 페이지 > 저장 > 장비 연결정보 삭제
*/
delete from
    equipment_link
where
    id = #{id}
    and eqp_manage_id = #{eqp_manage_id}

mapper.update:
/* updateBasicEqpList ...
    장비관리 > 장비목록 > 수정 페이지 > 저장 : 장비 기본정보 */
UPDATE
    equipment_basic
SET
    eqp_name = #{eqp_name}

    , host_name = #{host_name}


    , model_name = #{model_name}


    , m_company = #{m_company}


    , domestic = #{domestic}


    , redundancy_config = #{redundancy_config}


    , maintenance_contract_target = #{maintenance_contract_target}


    , config_id = #{config_id}


    , asset_id = #{asset_id}


    , sub_id = #{sub_id}


    , detail_id = #{detail_id}


    , network_operation_type = #{network_operation_type}


    , operating_status = #{operating_status}


    , operating_department = #{operating_department}


    , primary_operator = #{primary_operator}


    , secondary_operator = #{secondary_operator}


    , primary_outsourced_operator = #{primary_outsourced_operator}


    , secondary_outsourced_operator = #{secondary_outsourced_operator}


    , updated_at = NOW()

WHERE
    eqp_manage_id = #{eqp_manage_id};

/* updateDetailEqpList ...
    장비관리 > 장비목록 > 수정 페이지 > 저장 : 장비 세부정보 저장 */
UPDATE
    equipment_detail
SET
    serial_number = #{serial_number}

    , dbrain_number = #{dbrain_number}


    , CPU = #{cpu}


    , MEMORY = #{mem}


    , DISK = #{disk}


    , ip_address = #{ip_address}


    , OS = #{os_version}


    , acquisition_cost = #{acquisition_cost}


    , installation_coordinates = #{installation_coordinates}


    , installation_units = #{installation_units}


    , equipment_size_units = #{equipment_size_units}


    , asset_acquisition_date = #{asset_acquisition_date}


    , asset_disposal_date = #{asset_disposal_date}


    , eol_status = #{eol_status}


    , eos_status = #{eos_status}

WHERE
    eqp_manage_id = #{eqp_manage_id}

/* deleteEqpList ...
    장비관리 > 장비목록 > 삭제
    장비관리 > 장비목록 > 상세 페이지 > 삭제 */
update equipment_basic
set is_deleted = 'Y'
where eqp_manage_id = #{deleteEqpTarget}
